    fun isNameValid(name: String) = name.trim().length in 2..50
    fun isPhoneValid(phone: String) = phone.matches(Regex(PHONE_PATTERN))
    fun isEmailValid(email: String) = email.matches(Regex(EMAIL_PATTERN))
    fun isPasswordValid(password: String) = password.trim().length in 8..50
    fun isConfirmPasswordValid(password: String, confirmPassword: String) = confirmPassword == password
            && isPasswordValid(confirmPassword)

    fun isFormValid(
        firstName: String,
        lastName: String,
        email: String,
        phone: String,
        password: String,
        confirmPassword: String
    ) = isNameValid(firstName)
            && isNameValid(lastName)
            && isEmailValid(email)
            && isPhoneValid(phone)
            && isPasswordValid(password)
            && isConfirmPasswordValid(password, confirmPassword)

